rule.definition:
= with(
  local!commonDocumentLinkSubset: #"_a-0000da84-7836-8000-9ba2-011c48011c48_11155"(
    ri!commonDocumentId
  ),
  local!entityType: local!commonDocumentLinkSubset.entityType,
  local!entityId: local!commonDocumentLinkSubset.entityId,
  local!relationshipDataSubset: if(
    or(
      or(
        length(
          local!entityType
        ) = #"_a-0000da7e-55bc-8000-9ba2-011c48011c48_10895",
        isnull(
          local!entityType
        )
      ),
      or(
        length(
          local!entityId
        ) = #"_a-0000da7e-55bc-8000-9ba2-011c48011c48_10895",
        isnull(
          local!entityId
        )
      )
    ),
    {},
    apply(
      #"_a-0000da84-7836-8000-9ba2-011c48011c48_11242",
      merge(
        {
          local!entityType
        },
        {
          local!entityId
        }
      )
    )
  ),
  local!relationshipDataSubset
)
