rule.definition:
load(
    
  local!pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
    startIndex: 1,
    batchSize: 10,
    sort: #"SYSTEM_SYSRULES_sortInfo"(
    field: "streamText",
    ascending: true
    )
  ),
  
  local!careEvaluationDetails: if(
    #"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_12573"(ri!evaluationId_int),
    {},
    index(#"_a-0000d9e6-bfac-8000-9ba2-011c48011c48_1534"(ri!evaluationId_int), 1,{} )
  ),
  
  local!selectedFilter,
    
  with(

    local!questions: #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000dba5-d4f5-8000-9ba2-011c48011c48_23152",
      query:  #"SYSTEM_SYSRULES_query"(
      selection:  #"SYSTEM_SYSRULES_querySelection"(
        columns: {
        #"SYSTEM_SYSRULES_queryColumn"(field: "rowId"),
        #"SYSTEM_SYSRULES_queryColumn"(field: "streamText"),
        #"SYSTEM_SYSRULES_queryColumn"(field: "questionText"),
        #"SYSTEM_SYSRULES_queryColumn"(field: "justificationText")
        }
      ),
      logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
        operator: "AND",
        filters: {
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "evaluationId",
            operator: "=",
            value: ri!evaluationId_int
          ),
          #"SYSTEM_SYSRULES_queryFilter"(
            field: "questionTypeCode",
            operator: "=",
            value: #"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_15034"
          ),
          if(
            #"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_12573"(local!selectedFilter),
            {},
            #"SYSTEM_SYSRULES_queryFilter"(
            field: "streamCode",
            operator: "=",
            value: local!selectedFilter
            )
          )
        }
      ),
      pagingInfo: local!pagingInfo
      )
    ),
    
    
    #"SYSTEM_SYSRULES_dashboardLayout"(
      firstColumnContents: {
        
        #"62036449-0f0d-4655-b4e9-71bd89ffea77"(careEvaluation: local!careEvaluationDetails),
      
          #"SYSTEM_SYSRULES_sectionLayout"(
          label:"Risk Questions",
          firstColumnContents: {
            
            #"SYSTEM_SYSRULES_dropdownField"(
              label:  "Filter",
              labelPosition:"ABOVE",
              placeholderLabel: #"_a-0000dba5-d4f5-8000-9ba2-011c48011c48_23147",
              choiceLabels: {
                #"_a-0000dbaa-6fe4-8000-9ba2-011c48011c48_23383",
                #"_a-0000dbaa-6fe4-8000-9ba2-011c48011c48_23385", 
                #"_a-0000dbaa-6fe4-8000-9ba2-011c48011c48_23387", 
                #"_a-0000dbaa-6fe4-8000-9ba2-011c48011c48_23389", 
                #"_a-0000dbaa-6fe4-8000-9ba2-011c48011c48_23391"
              },
              choiceValues: {
        				#"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_15264",
        				#"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_15266",
        				#"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_15268", 
        				#"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_15270",
        				#"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_15272"
              },
              value: local!selectedFilter,
              saveInto: {
              a!save(local!selectedFilter, save!value),
              a!save(local!pagingInfo.startIndex, 1)
              }
            ),
            
            #"SYSTEM_SYSRULES_gridField"(
              totalCount: local!questions.totalCount,
              columns: {
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Stream",
                  field: "streamText",
                  data: index(local!questions.data, "streamText", null)
                ),
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Question",
                  field: "questionText",
                  data:  index(local!questions.data, "questionText", null),
                  links: apply(
                    #"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_14720",
                    index(
                      local!questions.data,
                      "rowId",
                      {}
                    ),
                    #"_a-0000db6c-d5e0-8000-9ba2-011c48011c48_15050"
                  )
                ),
                #"SYSTEM_SYSRULES_gridTextColumn"(
                  label: "Answer",
                  field: "justificationText",
                  data:  index(local!questions.data, "justificationText", null)
                )
              },
             value: local!pagingInfo,
             saveInto: local!pagingInfo
            )
          }
        )
      }
    )
  )
)

    /*cons!CARE_QUESTION_TYPE_RISK_EVALUATION*/
